mapper.select:
SELECT ROWNUM R, IID, NAME, PRICE, CURRENT_STOCK, ORIGIN_STOCK, STATUS, BRAND, CATEGORY, REGISTERDATE, UPDATEDATE, SAVINGLEVEL_ID, SAVINGLEVELNAME, COUNT_OF_USER FROM ( SELECT ROWNUM R, IID, NAME, PRICE, CURRENT_STOCK, ORIGIN_STOCK, STATUS, BRAND, CATEGORY, REGISTERDATE, UPDATEDATE, SAVINGLEVEL_ID, SAVINGLEVELNAME, COUNT_OF_USER FROM ( SELECT I.ID IID, I.NAME NAME, I.PRICE PRICE, I.CURRENT_STOCK CURRENT_STOCK, I.ORIGIN_STOCK ORIGIN_STOCK, I.STATUS STATUS, I.BRAND BRAND, I.CATEGORY CATEGORY, I.REGISTERDATE REGISTERDATE, I.UPDATEDATE UPDATEDATE, I.SAVINGLEVEL_ID SAVINGLEVEL_ID, SL.NAME SAVINGLEVELNAME, COUNT(DISTINCT U.ID) COUNT_OF_USER FROM ITEM I LEFT OUTER JOIN SAVINGLEVEL SL ON SL.ID = I.SAVINGLEVEL_ID LEFT OUTER JOIN ORDERS O ON O.ITEM_ID = I.ID LEFT OUTER JOIN USERS U ON O.USER_ID = U.ID GROUP BY I.ID, I.NAME, I.PRICE, I.CURRENT_STOCK, I.ORIGIN_STOCK, I.STATUS, I.BRAND, I.CATEGORY, I.REGISTERDATE, I.UPDATEDATE, I.SAVINGLEVEL_ID, SL.NAME
ORDER BY COUNT_OF_USER DESC
ORDER BY I.REGISTERDATE DESC
) ST
WHERE SAVINGLEVELNAME=#{level} AND CATEGORY=#{category}
WHERE SAVINGLEVELNAME=#{level}
WHERE CATEGORY=#{category}
) ST
WHERE R>=#{count} AND R<#{count}+12

SELECT I.ID, I.NAME, PRICE, CURRENT_STOCK, ORIGIN_STOCK, I.STATUS, BRAND, CATEGORY, REGISTERDATE, UPDATEDATE, I.SAVINGLEVEL_ID, SL.NAME SAVINGLEVELNAME
FROM ITEM I JOIN SAVINGLEVEL SL ON I.SAVINGLEVEL_ID = SL.ID
WHERE I.SAVINGLEVEL_ID = #{savingLevelId}
